Game = {start: function () {
    Crafty.init(Game.width(), Game.height());
    Crafty.background("rgb(82, 82, 82)");
    for (var a = 0; a < Game.map_grid.width; a++) {
        for (var b = 0; b < Game.map_grid.height; b++) {
            if (b < 4) {
                Crafty.e("Sky").at(a, b)
            } else {
                Crafty.e("Rock").at(a, b)
            }
        }
    }
    Crafty.e("PlayerCharacter").at(5, 3)
}, map_grid: {width: 24, height: 20, tile: {width: 16, height: 16}}, width: function () {
    return this.map_grid.width * this.map_grid.tile.width
}, height: function () {
    return this.map_grid.height * this.map_grid.tile.height
}};